import React, { Component } from "react";

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import { Grid } from "@material-ui/core/es";
import uploadIcon from "../assets/images/upload.png";
import downloadIcon from "../assets/images/download.png";
import deleteIcon from "../assets/images/delete.jpg";
import { CSVLink } from "react-csv";
import { useRef } from "react";

class Csv extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      showMenu: false,
      filterMethodString: props.filterMethodString,
      filterMethod: props.filterMethod,
      downloadData: [],
      changeFilterMethod: props.changeFilterMethod,
      reactTable: null
    };
  }

  showMenu(showMenu) {
    this.setState({ showMenu: !showMenu });
  }

  selectFilter(i, filterMethodString) {
    const { changeFilterMethod } = this.state;

    this.setState({
      showMenu: false,
      filterMethodString: filterMethodString,
      filterMethod: i
    });

    changeFilterMethod(i, filterMethodString);
  }

  uploadCsv() {
    const { putDataToDB, csvData, csvHeader, csvName } = this.props;

    var name = prompt("Please enter name for csv");

    putDataToDB(name, csvData, csvHeader);
  }

  deleteCsv() {
    const { deleteFromDB } = this.props;
    deleteFromDB();
  }

  downloadCsv() {
    const { csvData, csvHeader } = this.props;

    let downloadedData = [];
    let header = [];

    for (let j = 0; j < csvHeader.length; j++) {
      header.push(csvHeader[j].Header);
    }

    downloadedData.push(header);

    for (let i = 0; i < csvData.length; i++) {
      let body = [];

      for (let j = 0; j < csvHeader.length; j++) {
        let columnName = csvHeader[j].Header;
        body.push(csvData[i][columnName]);
      }
      downloadedData.push(body);
    }
    this.setState({ downloadData: downloadedData });
  }

  render() {
    const { showMenu, filterMethodString, downloadData } = this.state;
    const { csvData, csvHeader } = this.props;

    if (!csvData) {
      return <div />;
    }

    //  const currentRecords = this.selectTable.getResolvedState().sortedData;
    // console.log(currentRecords);

    return (
      <div>
        <Grid container>
          <Grid item xs={4}>
            <div>
              <button onClick={() => this.showMenu(showMenu)}>
                {"Filter Method"}
              </button>
              <span> {filterMethodString} </span>
            </div>
          </Grid>
          <Grid item xs={6} />
          <Grid item xs={2}>
            <img
              alt="weFantastic"
              src={uploadIcon}
              onClick={() => this.uploadCsv()}
              style={{
                width: "30px"
              }}
            />
            <CSVLink data={downloadData}>
              <img
                alt="weFantastic"
                src={downloadIcon}
                onClick={() => this.downloadCsv()}
                style={{
                  width: "26px",
                  marginLeft: "20px"
                }}
              />
            </CSVLink>
            <img
              alt="weFantastic"
              onClick={() => this.deleteCsv()}
              src={deleteIcon}
              style={{
                width: "22px",
                marginLeft: "20px"
              }}
            />
          </Grid>
        </Grid>

        {showMenu && (
          <div className="menu">
            <button onClick={() => this.selectFilter(0, "Starts With")}>
              {" "}
              Starts With
            </button>
            <button onClick={() => this.selectFilter(1, "Includes")}>
              {" "}
              Includes{" "}
            </button>
            <button onClick={() => this.selectFilter(2, "Not Includes")}>
              {" "}
              Not Includes{" "}
            </button>
            <button onClick={() => this.selectFilter(3, "Greater Than")}>
              {" "}
              Greater Than{" "}
            </button>
            <button onClick={() => this.selectFilter(4, "Lower Than")}>
              {" "}
              Lower Than{" "}
            </button>
          </div>
        )}

        {csvHeader && csvData && (
          <ReactTable
            style={{ marginTop: "20px" }}
            data={csvData}
            columns={csvHeader}
            defaultPageSize={10}
            className="-striped -highlight"
            ref={r => this.setState({ reactTable: r })}
          />
        )}
      </div>
    );
  }
}

export default Csv;
