import React, { Component } from "react";
import { makeCsvData } from "../utils/Utils";

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import CSVReader from "react-csv-reader";
import { Grid } from "@material-ui/core/es";

class Csv extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      showMenu: false,
      filterMethodString: "Starts With",
      filterMethod: 0
    };
  }

  showMenu(showMenu) {
    this.setState({ showMenu: !showMenu });
  }

  selectFilter(i, filterMethodString) {
    this.setState({
      showMenu: false,
      filterMethodString: filterMethodString,
      filterMethod: i
    });
  }

  render() {
    const { showMenu, filterMethodString } = this.state;
    const { csvData, csvHeader } = this.props;

    return (
      <div>
        <Grid container>
          <Grid item xs={4}>
            <div>
              <button onClick={() => this.showMenu(showMenu)}>
                {"Filter Method"}
              </button>
              <text> {filterMethodString} </text>
            </div>
          </Grid>
          <Grid item xs={7}>
            {"asdjk"}
          </Grid>
          <Grid item xs={1}>
            {"asdjk asdk alaaa ksd"}
          </Grid>
        </Grid>

        {showMenu && (
          <div className="menu" style={{ marginTop: "10px" }}>
            <button onClick={() => this.selectFilter(0, "Starts With")}>
              {" "}
              Starts With
            </button>
            <button onClick={() => this.selectFilter(1, "Ends With")}>
              {" "}
              Ends With{" "}
            </button>
            <button onClick={() => this.selectFilter(2, "Not Includes")}>
              {" "}
              Not Includes{" "}
            </button>
            <button onClick={() => this.selectFilter(3, "Exact Match")}>
              {" "}
              Exact Match{" "}
            </button>
          </div>
        )}

        {csvHeader && csvData && (
          <ReactTable
            style={{ marginTop: "20px" }}
            data={csvData}
            columns={csvHeader}
            defaultPageSize={10}
            className="-striped -highlight"
          />
        )}
      </div>
    );
  }
}

export default Csv;
