import React, { Component } from "react";
import { makeCsvData } from "../utils/Utils";

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import CSVReader from "react-csv-reader";

class Csv extends Component {
  constructor(props) {
    super(props);
    this.state = {
      csvData: props.csvData,
      csvHeader: props.csvHeader,
      loading: false,
      showMenu: false,
      filterMethodString: "Starts With",
      filterMethod: 0
    };
  }

  showMenu(showMenu) {
    this.setState({ showMenu: !showMenu });
  }

  selectFilter(i, filterMethodString) {
    this.setState({
      showMenu: false,
      filterMethodString: filterMethodString,
      filterMethod: i
    });
  }

  render() {
    const { csvData, csvHeader, showMenu, filterMethodString } = this.state;

    return (
      <div>
        <CSVReader
          cssClass="csv-reader-input"
          label="Csv Reader"
          onFileLoaded={this.uploadCsv}
          onError={this.handleDarkSideForce}
          inputId="csv"
          inputStyle={{ color: "red" }}
        />
        <div>
          <button
            style={{ marginTop: "12px" }}
            onClick={() => this.showMenu(showMenu)}
          >
            Filter Method
          </button>
          <text> {filterMethodString} </text>
        </div>

        {showMenu && (
          <div className="menu" style={{ marginTop: "10px" }}>
            <button onClick={() => this.selectFilter(0, "Starts With")}>
              {" "}
              Starts With
            </button>
            <button onClick={() => this.selectFilter(1, "Ends With")}>
              {" "}
              Ends With{" "}
            </button>
            <button onClick={() => this.selectFilter(2, "Not Includes")}>
              {" "}
              Not Includes{" "}
            </button>
            <button onClick={() => this.selectFilter(3, "Exact Match")}>
              {" "}
              Exact Match{" "}
            </button>
          </div>
        )}

        {csvHeader && csvData && (
          <ReactTable
            style={{ marginTop: "20px" }}
            data={csvData}
            columns={csvHeader}
            defaultPageSize={10}
            className="-striped -highlight"
          />
        )}
      </div>
    );
  }
}

export default Csv;
